mapper.select:
select f.name as projectName,
from po_guarantee_letter_require_req a left join wf_process_instance b on a.id = b.ENTITY_RECORD_ID left join hr_dept c on a.CRT_DEPT_ID = c.id left join ad_user d on b.START_USER_ID = d.id left join pm_party e on a.CUSTOMER_UNIT_ONE = e.id left join pm_prj f on a.PM_PRJ_IDS = f.id left join po_order_req g on a.PO_ORDER_REQ_IDS = g.id left join PM_EXP_TYPE h on a.PM_EXP_TYPE_IDS = h.id where a.status = 'ap' and a.PM_PRJ_IDS is not null and a.crt_dt >= '2022-11-22 00:00:00'
and find_in_set(#{projectId},a.PM_PRJ_IDS)
and a.GUARANTEE_DATE_TYPE_ID = #{guaranteeLetterTypeId}
and a.GUARANTEE_START_DATE >= #{guaranteeStartDate}
and a.GUARANTEE_END_DATE <= #{guaranteeEndDate}
order by a.id desc

select a.PROJECT_NAME_WR as projectName,
from po_guarantee_letter_require_req a left join wf_process_instance b on a.id = b.ENTITY_RECORD_ID left join hr_dept c on a.CRT_DEPT_ID = c.id left join ad_user d on b.START_USER_ID = d.id left join pm_party e on a.CUSTOMER_UNIT_ONE = e.id left join po_order_req g on a.PO_ORDER_REQ_IDS = g.id left join PM_EXP_TYPE h on a.PM_EXP_TYPE_IDS = h.id where a.status = 'ap' and a.PROJECT_NAME_WR is not null and a.crt_dt >= '2022-11-22 00:00:00'
and a.PROJECT_NAME_WR like concat('%',#{projectName},'%')
and a.GUARANTEE_DATE_TYPE_ID = #{guaranteeLetterTypeId}
and a.GUARANTEE_START_DATE >= #{guaranteeStartDate}
and a.GUARANTEE_END_DATE <= #{guaranteeEndDate}
order by a.id desc
