mapper.select:
SELECT a.id, c.DATE AS writeDate, a.VISUAL_PROGRESS AS progress, a.VISUAL_PROGRESS_DESCRIBE AS progressDescribe, a.PROCESS_REMARK_TEXT AS progressWeek, a.TEXT_REMARK_ONE AS progressRemark, ifnull(b.SYS_TRUE,'1') AS weatherStart, ifnull(b.IZ_END,'0') AS weatherCompleted, a.AD_USER_ID AS recordById, (select name from ad_user where id = a.ad_user_id) AS recordByName, a.FILE_ID_ONE AS fileId, b.PM_PRJ_ID AS projectId, e.name AS projectName, c.id as weekId, e.name as projectName, d.AD_USER_ID as manageUserId, f.name as manageUserName, b.IZ_WRITE as izWrite FROM PM_PROGRESS_WEEKLY_PRJ_DETAIL a LEFT JOIN PM_PROGRESS_WEEKLY_PRJ b ON a.PM_PROGRESS_WEEKLY_PRJ_ID = b.id LEFT JOIN pm_progress_weekly c ON a.PM_PROGRESS_WEEKLY_ID = c.id LEFT JOIN PM_PRJ e on b.pm_prj_id = e.id left join (select ad_user_id,PM_PRJ_ID from pm_roster where post_info_id = '1633997482885255168' and ad_user_id is not null GROUP BY PM_PRJ_ID,ad_user_id) d on b.pm_prj_id = d.PM_PRJ_ID LEFT JOIN ad_user f ON d.AD_USER_ID = f.id WHERE a.STATUS = 'ap' AND b.STATUS = 'ap' AND c.STATUS = 'ap'
and b.SYS_TRUE = #{weatherStart}
and b.IZ_END = #{weatherCompleted}
and a.VISUAL_PROGRESS >= #{progressMin}
and a.VISUAL_PROGRESS <= #{progressMax}
and f.name like concat('%',#{manageUserName},'%')
AND b.PM_PRJ_ID = #{projectId}
AND e.name like concat('%',#{projectName},'%')
and c.FROM_DATE >= #{writeDateMin}
and c.TO_DATE <= #{writeDateMax}
and c.id = #{weekId}
order by b.IZ_END asc,b.SYS_TRUE desc,b.PM_PRJ_ID desc
